column112:
/* Article */
#main article{	background-size:cover;	background-repeat:no-repeat;	background-position-y:0%;	background-blend-mode:multiply;	background-color:rgba(0,0,0,0);	color:#f9f9f9;	font-family:'Concert One', display;}/* Entry Content */
#main .hentry .entry-content{	font-family:'Rubik', sans-serif;	font-size:20px;}/* Post Title */
.hentry .entry-header h1{	color:#2c3e50;}/* Paragraph */
.hentry p{	color:#080606;}@media (min-width:922px){	/* Article */
	#main article{		padding-top:15px;	}	}

